import React from 'react';

import Image from '../assets/avatar.svg';
import {FaGithub, FaTelegram} from 'react-icons/fa';
import { TypeAnimation } from 'react-type-animation';
import { FlatTree, motion } from  'framer-motion';
import {fadeIn} from '../variants';

const Banner = () => {
  return (
  <section className='min-h-[95vh] lg:min-h-[78vh] flex items-center' 
  id='home'>
    <div className='container mx-auto'>
      <div className=' flex flex-col gap-y-8 lg:flex-row lg:items-center lg:gap-x-12'>
        {/* text */}
        <div className='flex-1 text-center font-secondary lg:text-left '>
          <motion.h1 
          variants={fadeIn('up', 0.3)} 
          initial="hidden" 
          viewport={{once: false, amount: 0.7}} 
          whileInView={'show'} 
          className='text-[55px] font-bold leading-[0.8] lg:text-[110px]'>
            Vlad <span>Kladkovoy</span>
          </motion.h1>
          <motion.div 
          variants={fadeIn('up', 0.4)} 
          initial="hidden" 
          viewport={{once: false, amount: 0.7}} 
          whileInView={'show'} 
          className='mb-6 text-[36px] lg:text-[60px] font-secondary font-semibold uppercase leading-[1]'>
            <span className='text-white mr-4'>I'm </span>
            <TypeAnimation sequence={[
              'Developer',
              2000,
              'Web-Developer',
              2000,
            ]}
            speed={50}
            className=' text-accent'
            wrapper='span'
            repeat={Infinity}
            />
          </motion.div>
          <motion.p 
          variants={fadeIn('up', 0.3)} 
          initial="hidden" 
          viewport={{once: false, amount: 0.7}} 
          whileInView={'show'} 
          className='mb-8 max-w-lg mx-auto lg:mx-0'>
            Lorem ipsum dolor sit amet consectetur
            adipisicing elit. Repudiandae nobis sunt animi,
            cupiditate ipsa voluptatibus? Ipsum iusto inventore 
            fugiat repudiandae.
          </motion.p>
          <div className='flex max-w-max gap-x-6 items-center mb-12 mx-auto lg:mx-0'>
            <button className='btn btn-lg'>Contact me</button>
            <a href="#" className='text-gradient btn-link'>
            My Portfolio
            </a>
          </div>
          {/* social */}
            <div className='flex text-[20px] gap-x-6 max-w-max mx-auto lg:mx-0'>
            <a href="#">
              <FaGithub />
            </a>
            <a href="#">
              <FaTelegram />
            </a>
            </div>
          </div>
        {/* img */}
        <div className='hidden lg:flex flex-1 max-w-[320px] lg:max-w-[482px]'>
          <img src={Image} alt="me"/>
        </div>
      </div>
    </div>
  </section>
  );
};

export default Banner;
