import React from 'react';
import CountUp from 'react-countup';
import { useInView } from 'react-intersection-observer';
import { motion } from 'framer-motion';
import {fadeIn} from '../variants'

const About = () => {
  const [ref, inView] = useInView ({
    threshold: 0.5, 
  });
  return (
  <section className='section' id='about' ref={ref}>
    <div className=' container mx-auto'>
      <div>
        {/* img */}
        <div className=' flex-1 bg-about bg-contain bg-no-repeat h-[640px]
        mix-blend-lighten bg-top'></div>
        {/* img */}
        <div>
          <h2>About me</h2>
          <h3>I'am a beginner front-end Developer</h3>
          <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Ratione corporis rerum dolore totam numquam voluptatem.
          </p>
          {/* stats */}
          <div className='flex'>
            <div>
              <div className='text-[40px] font-tertiary text-gradient mb-2'>
                {
                  inView ?
                  <CountUp start={0} end={13} duration={3} /> : null}
              </div>
              <div className=' font-primary text-sm tracking-[2px]'>
                ОПИСАНИЕ СЧЕТЧИКА
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </section>
  );
};

export default About;
