import React from 'react';
import CountUp from 'react-countup';
import { useInView } from 'react-intersection-observer';
import { motion } from 'framer-motion';
import {fadeIn} from '../variants'

const About = () => {
  const [ref, inView] = useInView ({
    threshold: 0.5, 
  });
  return (
  <section className='section' id='about' ref={ref}>
    <div className=' container mx-auto'>
      <div className='flex f'>
        {/* img */}
        <div className=' flex-1 bg-about bg-contain bg-no-repeat h-[640px]
        mix-blend-lighten bg-top'></div>
        {/* img */}
        <div className='flex-1'>
          <h2 className='h2 text-accent'>About me</h2>
          <h3 className='h3 mb-4'>I'am a beginner front-end Developer</h3>
          <p className='mb-6'>Lorem ipsum dolor sit amet consectetur adipisicing elit. Ratione corporis rerum dolore totam numquam voluptatem.
          </p>
          {/* stats */}
          <div className='flex gap-x-6 lg:gap-x-10 mb-12'>
            <div>
              <div className='text-[40px] font-tertiary text-gradient mb-2'>
                {
                  inView ?
                  <CountUp start={0} end={15} duration={3} /> : null}
              </div>
              <div className=' font-primary text-sm tracking-[2px]'>
                ОПИСАНИЕ СЧЕТЧИКА
              </div>
            </div>
            <div>
              <div className='text-[40px] font-tertiary text-gradient mb-2'>
                {
                  inView ?
                  <CountUp start={0} end={17} duration={3} /> : 
                  null}
                  k+
              </div>
              <div className=' font-primary text-sm tracking-[2px]'>
                ОПИСАНИЕ СЧЕТЧИКА(КОЛ_ВО ПРОЕКТОВ)
              </div>
            </div>
            <div>
              <div className='text-[40px] font-tertiary text-gradient mb-2'>
                {
                  inView ?
                  <CountUp start={0} end={20} duration={3} /> : null}
                k+
              </div>
              <div className=' font-primary text-sm tracking-[2px]'>
                ОПИСАНИЕ СЧЕТЧИКА(довольных клиентов)
              </div>
            </div>
          </div>
          <div className='flex gap-x-8 items-center'>
            <button className='btn btn-lg'>Contact me</button>
            <a href="#" className=' text-gradient btn-link'>
              My Portfolio
            </a>
          </div>
        </div>
      </div>
    </div>
  </section>
  );
};

export default About;
