import React from 'react';

import Image from '../assets/avatar.svg';
import {FaGithub, FaTelegram} from 'react-icons/fa';
import { TypeAnimation } from 'react-type-animation';
import { motion } from  'framer-motion';
import {fadeIn} from '../variants';

const Banner = () => {
  return <section className='section' id='home'>
    <div className='container mx-auto'>
      <div>
        {/* about */}
        <h1>Vlad <span>Kladkovoy</span>
        </h1>
        <div className='mb-6 text-[36px] lg:text-[60px] font-secondary font-semibold uppercase leading-[1]'>
          <span className='text-white mr-4'>I'm </span>
        <TypeAnimation sequence={[
          'Developer',
          2000,
          'Web-Developer',
          2000,
        ]}
        speed={50}
        className=' text-accent'
        wrapper='span'
        repeat={Infinity}
        />
        </div>
        <p>Lorem ipsum dolor sit amet consectetur 
          adipisicing elit. Repudiandae nobis sunt animi,
          cupiditate ipsa voluptatibus? Ipsum iusto inventore 
          fugiat repudiandae.
        </p>
        <div>
          <button className='btn btn-lg'>Contact me</button>
          <a href="#" className='text-gradient btn-link'>My Portfolio</a>
        {/* sicial */}
        <div className='flex text-[20px] gap-x-6 max-w-max mx-auto lg:'>
          <a href="#">
            <FaGithub />
          </a>
          <a href="#">
            <FaTelegram />
          </a>
        </div>
        </div>
        </div>
      <div>
        {/* img */}
        <img src={Image} alt="me" className='w-30 h-50 object-contain'/>
      </div>
    </div>
  </section>;
};

export default Banner;
